(function($){$.fn.toTopScroll=function(o){var s=$.extend({mh:120,ss:3,ft:1000},o);this.bind('click',function(){$('html,body').animate({scrollTop:0},$(window).scrollTop()/s.ss);});$(window).bind('scroll',function(){if($(window).scrollTop()>s.mh){this.fadeIn(s.ft);}else{this.fadeOut(s.ft);}}.bind(this));}})(jQuery);
